    public class LocalApi : IActivityApi
    {
        public override string ToString()
        {
            return $"Локальный источник";
        }

        private List<Post> Posts { get; set; }
        private List<Topic> Topics { get; set; }

        public LocalApi()
        {
            SetOfflineSource();
        }

        public async Task<Post> Create(Post post)
        {
            Posts.Add(post);
            return post;
        }
        public async Task<Topic> Create(Topic topic)
        {
            Topics.Add(topic);
            return topic;
        }

        public async Task<Post> Delete(Post post)
        {
            Posts.Remove(post);
            return post;
        }
        public async Task<Topic> Delete(Topic topic)
        {
            Topics.Remove(topic);
            return topic;
        }
        public async Task<IForum> GetForum()
        {
            return new Forum(Posts, Topics);
        }
        public async Task<IForum> GetForum(DateRange range)
        {
            var posts = Posts.Where(p => range.IsDateInside(p.Date));
            var topics = Posts.Select(p => p.TopicId).Distinct().Select(id => Topics.First(t => t.Id == id));
            return new Forum(posts, topics);
        }
        public async Task<Post> Update(Post post)
        {
            //
            return post;
        }
        public async Task<Topic> Update(Topic topic)
        {
            //
            return topic;
        }



